package basicJava150920;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 5;
		for (int i = 0; i < number; i++) {
			for (int j = 0; j < number; j++) {
		
				System.out.println(i);
			}

			System.out.println("");
		}
		System.out.println();

	}

}
