package basicJava150920;

public class PositiveNegativeNumber {

	
//a Java program to test a number is positive or negative.
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number1 = 0;
		
		if(number1 == 0)
		{
			System.out.println("Number entered is Zero:- " +number1);
		}

		else if (number1>0)
		{
			System.out.println("Number is Positive:- " +number1);
		}
		else
		{
			System.out.println("Number is Negative Number:- " +number1);
		}
	}

}
